import { Settings } from "./settings.js";

/**
 * A character sheet
 * @typedef {Object} CharacterSheet
 * @property {string} ucsid - unique character sheet id, generated by MongoDb
 * @property {string} participantID - unique participantID, generated by MongoDb
 */
class CharacterSheet {
    constructor(ucsid) {
        this.ucsid = ucsid;
    }
    /**
     * @property ucsid
     * @type {string}
     */
    ucsid = "";
    get getUcsid() {
        return this.ucsid;
    }
    set setUcsid(value) {
        this.ucsid = value;
    }
    /**
     * @property participantID
     * @type {string}
     */
    participantID = "";
    get getParticipantID() {
        return this.participantID;
    }
    set setParticipantID(value) {
        this.participantID = value;
    }
    
    /**
     * open tthe character sheet in a new tab
     * @function display
     */
    display = function () {
        window.open(Settings.getBaseURL + "character-sheet/" + participantID, '_blank');
    }
}


export {CharacterSheet}